import React from 'react';

const Card = ({Country}) => {
    return (
       <li className='card'>
       <img
       src={Country.flags.svg}
       alt={"drapeau " + Country.translation.fra.common}
       />
       
       <div className='infos'>
        <h2>{Country.translation.fra.common}</h2>
        <h4>{Country.capital}</h4>
       </div>
       <p>pop. {Country.population.toLocaleString()}</p>
       
       </li>
    );
};

export default Card;
